import React,{useState,useContext} from 'react';
import {FirebaseContext} from "../../firebase/index"

const Orden = ({orden}) => {

    // State para guardar el tiempo de entrega
    const[tiempoentrega,guardarTiempoEntrega] = useState(0)

    console.log("Desde el state de tiempo entrega", tiempoentrega)

    // context de Firebase
    const {firebase} = useContext(FirebaseContext)


    // Definir el tiempo de entrega en tiempo Real 
    const definirTiempo =(id)=>{
        try{
            firebase.db.collection("ordenes")
                                    .doc(id)
                                    .update({
                                        tiempoentrega : tiempoentrega,
                                    })
        }catch(error){
            console.log(error)
        }
    }


    return (
        <div className="sm:w-1/2 lg:w-1/3 px-2 mb-4">
            <div className="p-3 shadow-md bg-white">
                <h1 className="text-yellow-600 text-lg font-bold">{orden.id}</h1>

                {orden.orden.map(platillos =>(
                    <p className="text-gray-600">{platillos.cantidad} {platillos.nombre}</p>
                ))}

                <p className="text-gray-700 font-bold">Total a pagar : $ {orden.total}</p>

                {orden.tiempoentrega === 0 && (
                    <div className="mb-4">
                        <label className="block text-gray-700 text-sm font-bold mb-2">
                            Tiempo de Entrega
                        </label>

                        <input
                            type="number"
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            min="1"
                            max="20"
                            placeholder="20"
                            value={tiempoentrega}
                            onChange={e => guardarTiempoEntrega(parseInt(e.target.value))}

                        >

                        </input>

                        <button
                            onClick={()=>definirTiempo(orden.id)}
                            type="submit"
                            className="bg-gray-800 hover:bg-gray-900 w-full mt-5 text-white uppercase font-bold"

                        >

                            Definir Tiempo

                        </button>

                    </div>
                )}

            </div>

        </div>
    );
};

export default Orden;
